import React from 'react';
import Header from '../Home/Component/Header';
import Navbar from '../Home/Component/Navbar';
import Footer from '../Home/Component/Footer';
import Bottem from '../Home/Component/Bottem';
import CardUI from '../../Cards/CardUI';
import img1 from '../../images/image.webp';
import '../style.css';
import firebase from '../../config/firebase';
import { connect } from 'react-redux';

class Mobile_Phones extends React.Component {
    constructor() {
        super()
        this.state = {
          chat_user: {},
          OLX: [],

        }
        // this.get_messages();
      }
    get_messages = () => {
        firebase.database().ref('/').child("OLX").on('child_added', (OLX) => {
            console.log('OLX Data==>',OLX.val())
            this.state.OLX.push(OLX.val())
            this.setState({
                // Condition: this.state.Condition,
                // value1: this.state.value1,
                // Ad_title: this.state.Ad_title,
                // Description: this.state.Description,
                // Price: this.state.Price,
                // Url: this.state.Url,
                OLX: this.state.OLX,
            })
            // console.log('OLX ok Data==>',OLX.val())
        })
    }

    render() {
        return (
            <div>
                <Header />
                <button onClick={()=> this.get_messages()}><b>show sms</b> </button>
                <Navbar />
                <ul>
                    {this.state.OLX.map((v, i) => {
                        // return<li style={{marginLeft:v.uid===user.uid?"20px":"-20px"}} key={i}>{v.message}</li>
                        return  <div className="col-md-3">
                        <CardUI
                            featured="FEATURED"
                            imgsrc="https://www.google.com/url?sa=i&url=https%3A%2F%2Fmedium.com%2F%40dhilipkmr%2Fthe-loading-shimmer-f7129ac41894&psig=AOvVaw113c0qRa2d40Jlag8TpnJZ&ust=1605467763255000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCPDJm9Dfgu0CFQAAAAAdAAAAABAD"
                            title={v.Ad_title}
                            text={v.Description} />
                    </div>
                        // {let counter=counter + v}
                    })}
                </ul>

                <div className="container d-flex justify-center">
                    <br />

                    <h3 className="my-3">Mobile Phones</h3>
                </div>

                <div className="container d-flex justify-center">
                    <div className="row">
                        <div className="col-md-3">
                            <CardUI
                                featured="FEATURED"
                                imgsrc="https://apollo-singapore.akamaized.net:443/v1/files/wzeqqo69ko9m1-PK/image;s=280x0;q=60 320w, 
                                https://apollo-singapore.akamaized.net:443/v1/files/wzeqqo69ko9m1-PK/image;s=320x0;q=60 360w, 
                                https://apollo-singapore.akamaized.net:443/v1/files/wzeqqo69ko9m1-PK/image;s=335x0;q=60 375w, 
                                https://apollo-singapore.akamaized.net:443/v1/files/wzeqqo69ko9m1-PK/image;s=440x0;q=60 480w, 
                                https://apollo-singapore.akamaized.net:443/v1/files/wzeqqo69ko9m1-PK/image;s=640x0;q=60 720w, 
                                https://apollo-singapore.akamaized.net:443/v1/files/wzeqqo69ko9m1-PK/image;s=943x0;q=60 1024w, 
                                https://apollo-singapore.akamaized.net:443/v1/files/wzeqqo69ko9m1-PK/image;s=780x0;q=60 1025w"
                                title="17,000"
                                text="Alcatel 1SE" />
                        </div>
                        <div className="col-md-3">
                            <CardUI
                                featured="FEATURED"
                                imgsrc="https://www.google.com/url?sa=i&url=https%3A%2F%2Fmedium.com%2F%40dhilipkmr%2Fthe-loading-shimmer-f7129ac41894&psig=AOvVaw113c0qRa2d40Jlag8TpnJZ&ust=1605467763255000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCPDJm9Dfgu0CFQAAAAAdAAAAABAD"
                                title="Emptu"
                                text="Mudassir Mukhtar attari" />
                        </div>
                        <div className="col-md-3">
                            <CardUI
                                featured="FEATURED"
                                imgsrc="https://www.google.com/url?sa=i&url=https%3A%2F%2Fmedium.com%2F%40dhilipkmr%2Fthe-loading-shimmer-f7129ac41894&psig=AOvVaw113c0qRa2d40Jlag8TpnJZ&ust=1605467763255000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCPDJm9Dfgu0CFQAAAAAdAAAAABAD"
                                title="Emptu"
                                text="Mudassir Mukhtar attari" />
                        </div>
                        <div className="col-md-3">
                            <CardUI
                                featured="FEATURED"
                                imgsrc="https://www.google.com/url?sa=i&url=https%3A%2F%2Fmedium.com%2F%40dhilipkmr%2Fthe-loading-shimmer-f7129ac41894&psig=AOvVaw113c0qRa2d40Jlag8TpnJZ&ust=1605467763255000&source=images&cd=vfe&ved=0CAIQjRxqFwoTCPDJm9Dfgu0CFQAAAAAdAAAAABAD"
                                title="Emptu"
                                text="Mudassir Mukhtar attari" />
                        </div>
                    </div>
                </div>
                <Footer />
                <Bottem />

            </div>
        )
    }
}
// export default Mobile_Phones;
const mapStateToProps = (state) => ({
    create_user: state.create_user,
    users: state.users

})



export default connect(mapStateToProps, null)(Mobile_Phones);
