const INITIAL_STATE = {
    users: [
        {
            name: "lovely Mian",
            email: "mudassirmukhtar4@gmail.com"
        },
        {
            name: "Mudassir Mukhtar",
            email: "mudassirmukhtar4@gmail.com"
        }
    ]

}
export default (state = INITIAL_STATE, action) => {
    switch(action.type){
        case"set_data":
        return ({
            ...state,
            users:[...state.users,action.data],
        })  
        default:
            return state;      
    }
   
}
