import React from 'react';
import { Modal, Button, Row, Col, form, Container } from 'react-bootstrap';
import '../../App.css';
import img1 from '../../images/image.webp'
import Card from '../../Cards/CardUI'
import swal from 'sweetalert';
import firebase from '../../config/firebase';
import storage from '../../config/firebase';
import { connect } from 'react-redux';
import {render} from "react-dom"


class Addproduct extends React.Component {
  // constructor(){
  //   super()
  //   this.state={
  //     Condition: [""],
  //   value1: [""],
  //   Ad_title: [""],
  //   Description: [""],
  //   Price:[""]
  //       }
  // }
  // if (user === "") {
  //   alert("Enter yor add data")
  // }
  // else {
  //   alert("Plzzz sign in & try again")
  // }
  constructor(props) {
    super(props)
    this.state = {
      chat_user: {},
      olx: [],
      Condition: "",
      value1: "",
      Ad_title: "",
      Description: "",
      Price: "",
      value: "",
      file: null,
      image: null,
      Url:"",
      progress:0
    }
    this.handleChange=this
    .handleChange
    .bind(this);
    this.handleChange = this.handleChange.bind(this)
  }
  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  Post_Add = () => {
    let user = this.props.create_user;
    let uid = this.props.create_user.uid;


    if (this.state.Condition !== "") {
      if (this.state.value1 !== "") {
        if (this.state.Ad_title !== "") {
          if (this.state.Description !== "") {
            if (this.state.Price !== "") {

                firebase.database().ref('/').child("OLX").push({
                  Condition: this.state.Condition,
                  value1: this.state.value1,
                  Ad_title: this.state.Ad_title,
                  Description: this.state.Description,
                  Price: this.state.Price,
